import React, {useState} from 'react'
import './ExpenseForm.css'

export default function ExpenseForm(props){
    const [title, setTitle] = useState("")
    const [amount, setAmount] = useState("")
    const [date, setDate] = useState("")
    const [showForm, setShowForm] = useState(true)

    const titleChangeHandler = (event) => {
        setTitle(event.target.value)
    }
    const amountChangeHandler = (event) => {
        setAmount(event.target.value)
    }
    const dateChangeHandler = (event) => {
        setDate(event.target.value)
    }

    const submitHandler = (event) => {
        event.preventDefault();

        const eventData = {
            title: title,
            amount: amount,
            date: new Date(date)
        }
        props.onSaveExpenseData(eventData)
        setTitle('')
        setAmount('')
        setDate('')
    }

    const hideForm = () => {
        setShowForm(false)
    }

    const displayForm = () => {
        setShowForm(true)
    }

    return(
        <form onSubmit={submitHandler}>
        {showForm == true ? 

            (<form><div className="new-expense__controls">
                <div className="new-expense__control">
                    <label>Title</label>
                    <input type='text' value={title} onChange={titleChangeHandler}/>
                </div>
                <div className="new-expense__control">
                    <label>Amount</label>
                    <input type='number' value={amount} onChange={amountChangeHandler} min="0.01" step="0.01" />
                </div>
                <div className="new-expense__control">
                    <label>Date</label>
                    <input type='date' value={date} onChange={dateChangeHandler} min="2020-01-01" max="2022-12-31" />
                </div>
            </div>
            <div className="new-expense__control">
                <button type="submit" onClick={hideForm}>Add Expense</button>
                <button onClick={hideForm}>Cancel</button>
            </div>
            </form>)
        :
        (<div className="new-expense__control">
                <button type="submit">Add NewExpense</button>
            </div>)}
        </form>
    )
}
